import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  const [username, SetUserName] = useState("");
  const [userpass, SetUserPass] = useState("");
  useEffect(() => {
    let uname = localStorage.getItem("user_name");
    let upass = localStorage.getItem("user_password");
    if (uname && upass) {
      SetUserName(uname);
      SetUserPass(upass);
    }
  }, []);
  return (
    <div className="header">
      <div className="row">
        <div className="col-md-8">
          <h2>Django, React, MongoDB Crud Application</h2>
        </div>
        <div className="col-md-4" style={{ textAlign: "right" }}>
          <div className="row">
            {username ? (
              <div>
                <span>Welcome {username}</span>
                <Link to="/login" className="nav-link col-md-9">
                  Logout
                </Link>
              </div>
            ) : (
              <div>
                <Link to="/signup" className="nav-link col-md-9">
                  Signup
                </Link>
                <Link to="/login" className="nav-link col-md-1">
                  Login
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
