import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router";
import Button from "../../Button";

const DepartmentList = (props) => {
  const history = useHistory();

  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/all_department/").then((res) => {
      if (res) {
        setDepartments(res.data);
      }
    });
  }, []);

  const handleAdd = () => {
    history.push({
      pathname: "/add_department",
    });
  };

  const editHandleClick = (id) => {
    //console.log(id);
    history.push({
      pathname: "/update_department/" + id,
    });
  };

  const handleDelete = (id) => {
    axios
      .delete("http://127.0.0.1:8000/delete_department/" + id)
      .then((res) => {
        if (res) {
          console.log(res.data);
          window.location.reload();
        }
      });
  };
  return (
    <div>
      <h6>Department</h6>
      <div className="row">
        <div className="col-md-1">
          <Button
            type="button"
            className="btn btn-success btn-sm"
            onClick={handleAdd}
            label="Add"
          />
        </div>
      </div>
      <br />
      <div className="row">
        <div
          className="col-md-2"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Dept.Name
        </div>

        <div
          className="col-md-5"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Description
        </div>
        <div
          className="col-md-3"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Actions
        </div>
      </div>
      <br />
      {departments.map((d, index) => {
        return (
          <div className="row" key={index}>
            <div className="col-md-2">{d.department_name}</div>
            <div className="col-md-5">{d.description}</div>
            <div className="col-md-3">
              <Button
                type="button"
                className="btn btn-primary btn-sm m-1"
                label="Edit"
                onClick={(e) => editHandleClick(d.id)}
              />
              <Button
                type="button"
                className="btn btn-danger btn-sm m-1"
                label="Delete"
                onClick={(e) => handleDelete(d.id)}
              />
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default DepartmentList;
