import React, { useState, useEffect } from "react";
import axios from "axios";
import Input from "../../Input";
import Button from "../../Button";
import { useHistory } from "react-router";

const EditDepartment = (props) => {
  const history = useHistory();
  const [message, setMessage] = useState("");
  const [inputs, setInputs] = useState({
    department_name: "",
    description: "",
  });

  useEffect(() => {
    console.log(props.match.params);
    const id = props.match.params.id;
    axios
      .get("http://127.0.0.1:8000/get_department_by_id/", {
        params: { id: id },
      })
      .then((res) => {
        if (res) {
          console.log(res.data);
          setInputs({
            department_name: res.data[0].department_name,
            description: res.data[0].description,
          });
        }
      });
  }, []);
  const handleChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
  };
  const handleCancelExit = () => {
    history.push({
      pathname: "/all_department",
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // send data to django
    const data = {
      department_name: inputs.department_name,
      description: inputs.description,
    };
    if (inputs.department_name && inputs.description) {
      axios
        .put("http://127.0.0.1:8000/update_department/", data)
        .then((res) => {
          if (res) {
            setMessage("Department inserted successfully");
          } else {
            setMessage("Something wrong department is not inserted.");
          }
        });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group row">
        <label
          className="col-md-3 col-form-label"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Department Name:
        </label>
        <div className="col-md-9">
          <Input
            name="department_name"
            value={inputs.department_name}
            handleChange={handleChange}
            className="form-control form-control-md"
          />
        </div>
      </div>
      <div className="form-group row">
        <label
          className="col-md-3 col-form-label"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Description:
        </label>
        <div className="col-md-9">
          <Input
            name="description"
            value={inputs.description}
            handleChange={handleChange}
            className="form-control form-control-md"
          />
        </div>
      </div>
      <div className="form-group row">
        <label className="col-md-3 col-form-label"></label>
        <div className="col-md-9">
          <Button
            type="submit"
            className="btn btn-success btn-sm col-md-2"
            label="Save"
          />

          <Button
            type="button"
            className="btn btn-primary btn-sm col-md-2 m-1"
            onClick={handleCancelExit}
            label="Cancel/Exit"
          />
        </div>
      </div>
      <span>{message && <div style={{ color: "green" }}>{message}</div>}</span>
    </form>
  );
};

export default EditDepartment;
