import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router";

const DepartmentList = (props) => {
  const history = useHistory();

  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/all_department/").then((res) => {
      if (res) {
        console.log(res.data);
        setDepartments(res.data);
      }
    });
  }, []);

  const handleAdd = () => {
    history.push({
      pathname: "/add_department",
    });
  };
  return (
    <div>
      <div className="row">
        <div className="col-md-1">
          <button
            type="button"
            className="btn btn-success btn-sm"
            onClick={handleAdd}
            style={{width:'70px'}}
          >
            Add
          </button>

        </div>

      </div>
      <br />
      <div className="row">
        <div
          className="col-md-2"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Department name
        </div>

        <div
          className="col-md-5"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Description
        </div>
        <div
          className="col-md-3"
          style={{ fontWeight: "bold", fontStyle: "italic" }}
        >
          Actions
        </div>
      </div>
      <br />
      {departments.map((d, index) => {
        return (
          <div className="row" key={index}>
            <div className="col-md-2">{d.department_name}</div>
            <div className="col-md-5">{d.description}</div>
            <div className="col-md-3">
              <button type="button" className="btn btn-primary btn-sm m-1" style={{width:'70px'}}>Edit</button>
              <button type="button" className="btn btn-danger btn-sm m-1" style={{width:'70px'}}>Delete</button>
            </div>
            <hr />
          </div>
        );
      })}
    </div>
  );
};

export default DepartmentList;
