    <div className="header">
      <div className="row">
        <div className="col-md-7">
          <h2>Django, React, MongoDB Crud Application</h2>
        </div>
        <div className="col-md-5">
          {username ? (
            <div className="row" style={{ textAlign: "right" }}>
              <div className="dropdown show">
