                    Logout
                  </Link>
                  <a className="dropdown-item" href="#">
                    Something else here
                  </a>
                </div>
              </div>
            </div>
          ) : (
            <div className="row" style={{ textAlign: "right" }}>
              <Link to="/signup" className="nav-link col-md-10">
