import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import { FaBars, FaTimes } from "react-icons/fa";

const Header = () => {
  const history = useHistory();
  const [username, SetUserName] = useState("");
  const [click, setClick] = useState(false);

  const handleMenuClick = () => {
    setClick(!click);
  };
  useEffect(() => {
    let uname = localStorage.getItem("user_name");
    let upass = localStorage.getItem("user_password");
    if (uname && upass) {
      SetUserName(uname);
    }
  }, []);

  const handleClick = () => {
    localStorage.setItem("user_name", "");
    localStorage.setItem("user_password", "");
    window.location.reload();
    history.push({
      pathname: "/login",
    });
  };
  return (
    <div className="header">
      <div className="row">
        <div className="col-md-8">
          <h2>Django, React, MongoDB Crud Application</h2>
        </div>
        <div
          className="col-md-4"
          style={{ alignContent: "right", border: "solid" }}
        >
          <div className="row">
            {username ? (
              <div className="col-md-6" style={{ textAlign: "right" }}>
                Welcome {username}
              </div>
            ) : (
              <div>
                <Link to="/signup" className="nav-link">
                  Signup
                </Link>
                <Link to="/login" className="nav-link">
                  Login
                </Link>
              </div>
            )}
            {click ? (
              <div className="col-md-3">
                {username ? (
                  <div style={{ backgroundColor: "black" }}>
                    <div>
                      <FaTimes size={20} onClick={handleMenuClick} />
                    </div>
                    <div>
                      <Link to="" className="nav-link">
                        Change Password
                      </Link>
                      <Link to="" onClick={handleClick} className="nav-link">
                        Logout
                      </Link>
                    </div>
                  </div>
                ) : (
                  <div>
                    <Link to="/signup" className="nav-link">
                      Signup
                    </Link>
                    <Link to="/login" className="nav-link">
                      Login
                    </Link>
                  </div>
                )}
              </div>
            ) : (
              <div style={{ textAlign: "right" }} className="col-md-2">
                <FaBars size={20} onClick={handleMenuClick} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
