import React, { useState } from "react";
import axios from "axios";
import Input from "./Input";

const Form = () => {
  const [value, setValue] = useState("");
  const [message, setMessage] = useState("");
  const [inputs, setInputs] = useState({
    department_name: "",
    description: "",
  });
  const handleChange = (e) => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // send data to django
    const data = {
      department_name: inputs.department_name,
      description: inputs.description,
    };
    if (inputs.department_name && inputs.description) {
      axios.post("http://127.0.0.1:8000/add_department/", data).then((res) => {
        if (res) {
          setMessage("Department inserted successfully");
        } else {
          setMessage("Something wrong department is not inserted.");
        }
      });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label">Department Name:</label>
        <div className="col-sm-9">
          <Input
            name="department_name"
            value={inputs.department_name}
            handleChange={handleChange}
            className="form-control form-control-sm"
          />
        </div>
      </div>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label">Description:</label>
        <div className="col-sm-9">
          <Input
            name="description"
            value={inputs.description}
            handleChange={handleChange}
            className="form-control form-control-sm"
          />
        </div>
      </div>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label"></label>
        <div className="col-sm-9">
          <button type="submit">Save</button>
        </div>
      </div>
      <span>{message && <div style={{color:"green"}}>message</div> }</span>
    </form>
  );
};

export default Form;
