import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import { FaBars, FaTimes } from "react-icons/fa";

const Header = () => {
  const history = useHistory();
  const [username, SetUserName] = useState("");
  const [click, setClick] = useState(false);

  const handleMenuClick = () => {
    setClick(!click);
  };
  useEffect(() => {
    let uname = localStorage.getItem("user_name");
    let upass = localStorage.getItem("user_password");
    if (uname && upass) {
      SetUserName(uname);
    }
  }, []);

  const handleClick = () => {
    localStorage.setItem("user_name", "");
    localStorage.setItem("user_password", "");
    window.location.reload();
    history.push({
      pathname: "/login",
    });
  };
  return (
    <div className="header">
      <div className="row">
        <div className="col-md-8">
          <h2>Django, React, MongoDB Crud Application</h2>
        </div>

        <div className="col-md-4">
          <div className="row">
            <div className="col">
              <span>{username && <div>Welcome {username}</div>}</span>
            </div>
            <div className="col" style={{ textAlign: "right" }}>
              {username ? (
                <div className="dropdown show">
                  <a
                    className="btn btn-secondary dropdown-toggle"
                    href="#"
                    role="button"
                    id="dropdownMenuLink"
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    User
                  </a>
                  <div
                    className="dropdown-menu"
                    aria-labelledby="dropdownMenuLink"
                  >
                    <Link to="" className="dropdown-item">
                      Change Password
