                    <Link to="" onClick={handleClick} className="dropdown-item">
                      Logout
                    </Link>
                    <a className="dropdown-item" href="#">
                      Something else here
                    </a>
                  </div>
                </div>
              ) : (
                <div>
                  <Link to="/signup" className="nav-link">
                    Signup
