import React, { useState } from "react";
import Input from "./Input";

const Form = () => {
  const [value, setValue] = useState("");
  const handleChange = (e) => {
    setValue(e.target.value);
  };
  return (
    <form>
      <div class="form-group row">
        <label for="departmentName" class="col-sm-2 col-form-label">
          Department Name:
        </label>
        <div class="col-sm-10">
        <Input name="department-name" value={value} handleChange={handleChange} />        </div>
      </div>
      <div class="form-group row">
        <label for="inputPassword" class="col-sm-2 col-form-label">
          Password
        </label>
        <div class="col-sm-10">
          <input
            type="password"
            class="form-control"
            id="inputPassword"
            placeholder="Password"
          />
        </div>
      </div>


      <Input name="description" value={value} handleChange={handleChange} />
    </form>
  );
};

export default Form;
