import React, { useState } from "react";
import axios from "axios"
import Input from "./Input";

const Form = () => {
  const [value, setValue] = useState("");
  const [inputs, setInputs] = useState({department_name:"",description:""});
  const handleChange = (e) => {
    setInputs({
        ...inputs,[e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // send data to django
    const data ={
        "department_name":inputs.department_name,
        "description":inputs.description,
    }

    const result = axios.post("http://127.0.0.1:8000/add_department/",data)

  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label">Department Name:</label>
        <div className="col-sm-9">
          <Input
            name="department_name"
            value={inputs.department_name}
            handleChange={handleChange}
            className="form-control form-control-sm"
          />
        </div>
      </div>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label">Description:</label>
        <div className="col-sm-9">
          <Input
            name="description"
            value={inputs.description}
            handleChange={handleChange}
            className="form-control form-control-sm"
          />
        </div>
      </div>
      <div className="form-group row">
        <label className="col-sm-3 col-form-label"></label>
        <div className="col-sm-9">
          <button type="submit">Save</button>
        </div>
      </div>

    </form>
  );
};

export default Form;
