import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:8000/all_department/").then((res) => {
      if (res) {
        console.log(res.data);
        setDepartments(res.data);
      }
    });
  }, []);

  return (
    <div>
        <div className="row">
            <div className="col-md-1">
            <button type="button" className="btn btn-success btn-sm" >Add</button>  
            </div>
        </div>
      <div className="row">
        <div className="col-md-3" style ={{fontWeight:'bold',fontStyle:'italic'}}>Department name</div>
        <div className="col-md-5" style ={{fontWeight:'bold',fontStyle:'italic'}}>Description</div>
        <div className="col-md-2" style ={{fontWeight:'bold',fontStyle:'italic'}}>Actions</div>
      </div>
      {departments.map((d, index) => {
        return (
          <div className="row" key={index}>
            <div className="col-md-3">{d.department_name}</div>
            <div className="col-md-5">{d.description}</div>
            <div className="col-md-2">
              <button type="button">Edit</button>
              <button type="button">Delete</button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default DepartmentList;
