import React from "react";

const Select = ({ name, departments, handleChange, className }) => {
  return (
    <select onChange={handleChange} name={name} className={className}>
      {departments.map((dept, index) => {
        return (
          <option value={dept.id} key={index}>
            {dept.department_name}
          </option>
        );
      })}
    </select>
  );
};

export default Select;
